package com.example.recipesapp.LogSig

import android.content.Intent
import android.os.Bundle
import android.widget.Toast
import androidx.activity.viewModels
import androidx.appcompat.app.AppCompatActivity
import com.example.recipesapp.databinding.ActivitySignupBinding

class SignupActivity : AppCompatActivity() {
    private lateinit var binding: ActivitySignupBinding
    private val authViewModel: AuthViewModel by viewModels()

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivitySignupBinding.inflate(layoutInflater)
        setContentView(binding.root)

        authViewModel.signupResult.observe(this) { insertRowId ->
            if (insertRowId != -1L) {
                Toast.makeText(this, "Access allowed", Toast.LENGTH_SHORT).show()
                startActivity(Intent(this, LoginActivity::class.java))
                finish()
            } else {
                Toast.makeText(this, "Access error", Toast.LENGTH_SHORT).show()
            }
        }

        binding.signupButton.setOnClickListener {
            val username = binding.signupUsername.text.toString()
            val password = binding.signupPassword.text.toString()
            val email = binding.signupEmail.text.toString()

            var isValid = true

            // Email validacion
            if (!email.contains("@")) {
                binding.signupEmail.error = "Email must contain '@'"
                isValid = false
            }

            // Validacion de contraseña
            val passwordRegex = Regex("^[A-Z].{5,}$") // Empezar con mayúscula y al menos 6 caracteres
            if (!passwordRegex.matches(password)) {
                binding.signupPassword.error = "Password must start with uppercase and be at least 6 characters"
                isValid = false
            }

            // Validacion de nombre de usuario
            if (username.isBlank()) {
                binding.signupUsername.error = "Username cannot be empty"
                isValid = false
            }

            if (isValid) {
                val profilePic = "data:image/jpeg;base64,"
                authViewModel.signup(username, email, password, profilePic)
            }
        }


        binding.loginRedirect.setOnClickListener {
            startActivity(Intent(this, LoginActivity::class.java))
            finish()
        }
    }
}
